import React, { useState, useEffect, useRef } from 'react';
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
  LinearScale,
  CategoryScale,
  LineController,
} from 'chart.js';
import { Radar, Line } from 'react-chartjs-2';
import './App.css';

// Register Chart.js components
ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
  LinearScale,
  CategoryScale,
  LineController
);

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [sensorData, setSensorData] = useState({
    accelX: '--', accelY: '--', accelZ: '--',
    gyroX: '--', gyroY: '--', gyroZ: '--',
    accelMag: '--', gyroMag: '--',
  });
  const [currentPrediction, setCurrentPrediction] = useState({
    action: 'Loading...', confidence: '--', timestamp: ''
  });
  const [predictionHistory, setPredictionHistory] = useState([]);
  const [accelTimeSeriesData, setAccelTimeSeriesData] = useState([]);
  const [gyroTimeSeriesData, setGyroTimeSeriesData] = useState([]);

  const ws = useRef(null);

  useEffect(() => {
    function connectWebSocket() {
      // Replace with your WebSocket server address
      ws.current = new WebSocket('ws://localhost:8081'); 

      ws.current.onopen = () => {
        console.log('WebSocket Connected');
        setIsConnected(true);
      };

      ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        console.log('Message from server:', data);

        // Update Current Prediction
        if (data.action && data.confidence !== undefined) {
          setCurrentPrediction({
            action: data.action,
            confidence: `${(data.confidence * 100).toFixed(2)}%`,
            timestamp: `Cập nhật lúc: ${new Date().toLocaleTimeString()}`,
          });

          // Add to Prediction History (only if it's a prediction result)
          setPredictionHistory(prevHistory => [
            { time: new Date().toLocaleTimeString(), action: data.action, confidence: `${(data.confidence * 100).toFixed(2)}%` },
            ...prevHistory.slice(0, 9) // Keep last 10 predictions
          ]);
        }

        // Update Real-time Sensor Data and Time Series Data
        if (data.accelX !== undefined) {
          const accelMag = Math.sqrt(data.accelX**2 + data.accelY**2 + data.accelZ**2);
          const gyroMag = Math.sqrt(data.gyroX**2 + data.gyroY**2 + data.gyroZ**2);

          setSensorData({
            accelX: data.accelX.toFixed(4),
            accelY: data.accelY.toFixed(4),
            accelZ: data.accelZ.toFixed(4),
            gyroX: data.gyroX.toFixed(4),
            gyroY: data.gyroY.toFixed(4),
            gyroZ: data.gyroZ.toFixed(4),
            accelMag: accelMag.toFixed(4),
            gyroMag: gyroMag.toFixed(4),
          });

          // Add current data point to time series history (keep last 100 points)
          setAccelTimeSeriesData(prevData => [
            ...prevData.slice(prevData.length > 100 ? 1 : 0),
            {
              time: new Date().toLocaleTimeString(),
              x: data.accelX,
              y: data.accelY,
              z: data.accelZ,
            }
          ]);
          setGyroTimeSeriesData(prevData => [
            ...prevData.slice(prevData.length > 100 ? 1 : 0),
            {
              time: new Date().toLocaleTimeString(),
              x: data.gyroX,
              y: data.gyroY,
              z: data.gyroZ,
            }
          ]);
        }
      };

      ws.current.onclose = (event) => {
        console.log('WebSocket Disconnected', event);
        setIsConnected(false);
        // Attempt to reconnect after a delay
        setTimeout(connectWebSocket, 5000);
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket Error:', error);
        setIsConnected(false);
        if (ws.current) {
          ws.current.close();
        }
      };
    }

    connectWebSocket();

    // Cleanup function to close WebSocket on component unmount
    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount

  const radarChartData = {
    labels: ['AccelX', 'AccelY', 'AccelZ', 'GyroX', 'GyroY', 'GyroZ'],
    datasets: [{
      label: 'Giá trị cảm biến hiện tại',
      data: [
        parseFloat(sensorData.accelX) || 0,
        parseFloat(sensorData.accelY) || 0,
        parseFloat(sensorData.accelZ) || 0,
        parseFloat(sensorData.gyroX) || 0,
        parseFloat(sensorData.gyroY) || 0,
        parseFloat(sensorData.gyroZ) || 0,
      ],
      backgroundColor: 'rgba(0, 123, 255, 0.5)',
      borderColor: 'rgba(0, 123, 255, 1)',
      borderWidth: 1,
    }],
  };

  const radarChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      r: {
        angleLines: {
          display: false
        },
        suggestedMin: -10, // Adjust based on expected sensor value range
        suggestedMax: 10
      }
    },
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: false,
        text: 'Sensor Data Radar Chart'
      }
    }
  };

  const accelLineChartData = {
    labels: accelTimeSeriesData.map(data => data.time),
    datasets: [
      {
        label: 'AccelX',
        data: accelTimeSeriesData.map(data => data.x),
        borderColor: 'rgba(0, 123, 255, 1)',
        backgroundColor: 'rgba(0, 123, 255, 0.5)',
        tension: 0.1,
      },
      {
        label: 'AccelY',
        data: accelTimeSeriesData.map(data => data.y),
        borderColor: 'rgba(40, 167, 69, 1)',
        backgroundColor: 'rgba(40, 167, 69, 0.5)',
        tension: 0.1,
      },
      {
        label: 'AccelZ',
        data: accelTimeSeriesData.map(data => data.z),
        borderColor: 'rgba(220, 53, 69, 1)',
        backgroundColor: 'rgba(220, 53, 69, 0.5)',
        tension: 0.1,
      },
    ],
  };

  const gyroLineChartData = {
    labels: gyroTimeSeriesData.map(data => data.time),
    datasets: [
      {
        label: 'GyroX',
        data: gyroTimeSeriesData.map(data => data.x),
        borderColor: 'rgba(23, 162, 184, 1)',
        backgroundColor: 'rgba(23, 162, 184, 0.5)',
        tension: 0.1,
      },
      {
        label: 'GyroY',
        data: gyroTimeSeriesData.map(data => data.y),
        borderColor: 'rgba(255, 193, 7, 1)',
        backgroundColor: 'rgba(255, 193, 7, 0.5)',
        tension: 0.1,
      },
      {
        label: 'GyroZ',
        data: gyroTimeSeriesData.map(data => data.z),
        borderColor: 'rgba(108, 117, 125, 1)',
        backgroundColor: 'rgba(108, 117, 125, 0.5)',
        tension: 0.1,
      },
    ],
  };

  const lineChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Real-time Accelerometer and Gyroscope Data',
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Sensor Value',
        },
      },
    },
  };

  return (
    <div className="container">
      <h1 className="text-center mb-4">Fall Detection Dashboard</h1>

      <div className="row">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Trạng thái kết nối</div>
            <div className="card-body">
              <p id="connectionStatus" className={isConnected ? 'connected' : 'disconnected'}>
                {isConnected ? 'Connected' : 'Disconnected'}
              </p>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Dự đoán hành động hiện tại</div>
            <div className="card-body" id="currentPrediction">
              <h3>{currentPrediction.action}</h3>
              <p>{currentPrediction.confidence}</p>
              <small>{currentPrediction.timestamp}</small>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Giá trị cảm biến (Realtime)</div>
            <div className="card-body">
              <table className="table table-bordered table-striped" id="sensorDataTable">
                <thead>
                  <tr>
                    <th>Loại</th>
                    <th>X</th>
                    <th>Y</th>
                    <th>Z</th>
                    <th>Độ lớn</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Accelerometer</td>
                    <td>{sensorData.accelX}</td>
                    <td>{sensorData.accelY}</td>
                    <td>{sensorData.accelZ}</td>
                    <td>{sensorData.accelMag}</td>
                  </tr>
                  <tr>
                    <td>Gyroscope</td>
                    <td>{sensorData.gyroX}</td>
                    <td>{sensorData.gyroY}</td>
                    <td>{sensorData.gyroZ}</td>
                    <td>{sensorData.gyroMag}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Biểu đồ radar giá trị cảm biến</div>
            <div className="card-body">
              <Radar data={radarChartData} options={radarChartOptions} />
            </div>
          </div>
        </div>
      </div>

      {/* New Row for Line Chart */}
      <div className="row mt-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Biểu đồ gia tốc (Realtime)</div>
            <div className="card-body">
              <div style={{ height: '300px' }}>
                <Line data={accelLineChartData} options={lineChartOptions} />
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <div className="card">
            <div className="card-header">Biểu đồ con quay hồi chuyển (Realtime)</div>
            <div className="card-body">
              <div style={{ height: '300px' }}>
                <Line data={gyroLineChartData} options={lineChartOptions} />
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header">Lịch sử dự đoán</div>
            <div className="card-body">
              <table className="table table-bordered table-striped" id="predictionHistoryTable">
                <thead>
                  <tr>
                    <th>Thời gian</th>
                    <th>Hành động</th>
                    <th>Độ tin cậy</th>
                  </tr>
                </thead>
                <tbody>
                  {predictionHistory.map((prediction, index) => (
                    <tr key={index}>
                      <td>{prediction.time}</td>
                      <td>{prediction.action}</td>
                      <td>{prediction.confidence}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
